import React, { useEffect, useState } from 'react';
import Link from '@mui/material/Link';
import Title from './Title';
import RemoveIcon from '@mui/icons-material/Remove';
import { getUserArchiveDB } from '../../axios/detail/contentsLogic';
import { Box, Button, Card, CardContent, Checkbox, FormControlLabel, Grid, IconButton, Typography } from '@mui/material';
import { firebaseStorage } from '../../utils/firebase';
import { useNavigate } from 'react-router';

export default function MovieArchive({ userInfo }) {
  const userId = userInfo.userId;
  const navigate = useNavigate();
  const [userMovieArchive, setUserMovieArchive] = useState([]); 
  const [posterUrls, setPosterUrls] = useState([]);
  const [checked, setChecked] = useState([]);

  useEffect(() => {
    const getUserArchive = async () => {
      try {
        const res = await getUserArchiveDB(userId);
        setUserMovieArchive(res);
      } catch (error) {
        console.log('유저 보관함 로드 실패 : ', error);
      }
    };
    if (userId !== undefined) {
      getUserArchive();
    }
  }, [userId]);


  useEffect(() => {
    const getPosterUrls = async () => {
      const urls = await Promise.all(userMovieArchive.map(async (movie) => {
        const storageRef = firebaseStorage.ref(`poster/${movie.movieId}${movie.movieSeq}.jpg`);
        try {
          const url = await storageRef.getDownloadURL();
          return url;
        } catch (error) {
          console.log("포스터 URL 페칭 에러 : " , error);
          return null;
        }
      }));
      setPosterUrls(urls);
    };

    getPosterUrls();
  }, [userMovieArchive]);


  // Checkbox 선택 상태 변경 함수
  const handleToggle = (index) => {
    const newChecked = [...checked];
    newChecked[index] = !newChecked[index];
    setChecked(newChecked);
  };

  // 선택 삭제 함수
  const handleDeleteSelected = () => {
    const selectedMovies = userMovieArchive.filter((movie, index) => checked[index]);
    console.log('선택된 영화 삭제:', selectedMovies);
  };


  {/* Style */}
  const archiveSectiontyle = {
    minWidth: '1200px',
    maxWidth: '1200px',
    minHeight: '500px',
    marginTop: '20px',
    display: 'flex',
    flexDirection: 'row', 
    flexWrap: 'wrap', 
  };

  return (
    <>
    <div style={{display:'flex'}}>
      <Title>보관함</Title>
      <div style={{ marginLeft:'15px' }}>
      {userMovieArchive.length > 0 && (
        <Button variant="contained">
        <IconButton>
         <RemoveIcon />
          전체 삭제
        </IconButton>
          </Button>
        )}
        </div>
        <div style={{marginLeft:'10px'}}>
        {userMovieArchive.length > 0 && (
          <FormControlLabel
          control={<Checkbox checked={checked.every((value) => value)} onChange={() => setChecked(checked.map(() => !checked[0]))} />}
          label="선택 삭제"
          />
          )}
          </div>

    </div>


      <div style={archiveSectiontyle}>
      {userMovieArchive.length > 0 ? ( 
        userMovieArchive.map((movie, index) => (
          <div key={index}>
            <Card
              sx={{
                maxWidth: 240,
                height: 430,
                mx: 2,
                border: 'none',
                marginRight: '10px',
                cursor: 'pointer',
              }}
              onClick={() => navigate(`/movieDetail/${movie.movieId}${movie.movieSeq}`)}
            >
              <img src={posterUrls[index]} alt={movie.title} style={{ width: '100%', height: '350px', borderRadius: '3px 3px 3px 3px' }} />
              <CardContent style={{ border: 'none', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start' }}>
                <Typography variant="subtitle2" sx={{ fontSize: 15 }}>
                  {movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}
                </Typography>
                <Grid container justifyContent="flex-start" style={{marginTop:'5px'}}>
                <Typography variant="body2" style={{fontSize:'13px'}}>{movie.addDate} 추가됨</Typography>
              </Grid>
              </CardContent>
            </Card>
          </div>
        ))
      ) : (
        <p>아직 보관한 영화가 없습니다.</p>
      )}
      </div>
      <Link color="primary" href="/main" sx={{ mt: 3 }}>
        See more Movies
      </Link>
    </>
  );
}
