import React, { useEffect, useState } from 'react'
import { directorInfoByNameDB, directorInfoDB } from '../../axios/main/movieLogic'

const MovieDetailMidSection2 = ({ movieDetail }) => {
  const [directors, setDirectors] = useState([])
  console.log(directors);

  useEffect(() => {
    const getDirectorInfo = async (directorId) => {
      try {
        const res = await directorInfoDB(directorId)
        return res;
      } catch (error) {
        console.log('감독 정보 로드 실패 : ', error);
        return null;
      }
    }

    const fetchDirectorsInfo = async () => { 
      const directorIds = movieDetail.directorId ? movieDetail.directorId.split(',') : [];
      const directorNms = movieDetail.directorNm ? movieDetail.directorNm.split(',') : [];

      const directorsInfo = await Promise.all(
        directorIds.map(async (directorId, index) => {
          if (directorId) {
            return await getDirectorInfo(directorId);
          } else if (directorNms[index]) {
            return await getDirectorInfoByName(directorNms[index]);
          }
          return null;
        })
      );
      setDirectors(directorsInfo.filter(info => info !== null));
      console.log(directors);
    };
    fetchDirectorsInfo();
  }, [movieDetail.directorId])


  
  const getDirectorInfoByName = async (directorNm) => {
    try {
      const res = await directorInfoByNameDB(directorNm);
      return res;
    } catch (error) {
      console.log('감독 정보 로드 실패 : ', error);
      return null;
    }
  };



  return (
    <>
    
    </>
  )
}

export default MovieDetailMidSection2
