import React, { useState } from 'react'
import {
  Button,
  CssBaseline,
  TextField,
  FormControl,
  FormHelperText,
  Grid,
  Box,
  Typography,
  Container,
  Link,
  Alert,
  AlertTitle,
} from '@mui/material/';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router';
import { styled } from 'styled-components';
import { userLoginDB } from '../../axios/user/loginLogic';
import { serialize } from 'cookie';
import GoogleLogin from 'react-google-login';

// mui의 css 우선순위가 높기때문에 important를 설정 - 실무하다 보면 종종 발생 우선순위 문제
const FormHelperTexts = styled(FormHelperText)`
width: 100%;
padding-left: 16px;
font-weight: 700 !important;
color: #d32f2f !important;
`;

const Boxs = styled(Box)`
padding-bottom: 40px !important;
`;


const Login = () => {
  const theme = createTheme();
  
  const navigate = useNavigate();
  const [id , setId] = useState('');
  const [pw , setPw] = useState('');

  console.log(id);
  console.log(pw);

  const GoogleLoginButton = ({ onClick, disabled }) => (
    <button onClick={onClick} disabled={disabled} style={{ cursor: 'pointer', border: 'none', background: 'none' }}>
      <img src="/path/to/google_login_image.png" alt="구글 로그인" style={{ width: '100%', height: '100%' }} />
    </button>
  );
  

  {/* 구글 로그인 */}
  // 구글 로그인 성공 콜백
  const handleGoogleLoginSuccess = async (response) => {
    try {
      // 구글 로그인 성공 응답을 처리합니다.
      // 구글 로그인을 위한 별도의 API 엔드포인트가 있다고 가정합니다.
      const googleLoginData = {
        tokenId: response.tokenId,
      };
      const res = await userLoginDB(googleLoginData);
      console.log('Google 로그인 결과:', res);

      if (res === 1) {
        // 구글 로그인이 성공적으로 처리됐을 때의 처리 (일반 로그인 처리와 유사)
        document.cookie = serialize('userId', id, { path: '/' });
        console.log(document.cookie);
        navigate('/main');
      } else {
        // 구글 로그인이 실패했을 때의 처리
        console.log('Google 로그인 실패');
      }
    } catch (error) {
      console.error('Google 로그인 에러:', error);
    }
  };

  // 구글 로그인 실패 콜백
  const handleGoogleLoginFailure = (error) => {
    console.error('Google 로그인 에러:', error);
  };






  const handleLogin = async(e) => {
    e.preventDefault();
   
    try {
      const loginData = {
        userId : id,
        userPw : pw
      }
      const res = await userLoginDB(loginData)
      console.log('로그인 결과:', res);
      if(res == 1){
        <Alert severity="success" onClose={() => {}}>
        <AlertTitle>로그인 성공!</AlertTitle>
      </Alert>
        //쿠키에 로그인 상태 저장 (쿠키이름,값,경로허용)
        document.cookie = serialize('userId', id, { path: '/' });
        console.log(document.cookie);
        navigate('/main')
      }else{
        <Alert severity="warning" onClose={() => {}}>
  입력 정보를 다시 확인해주세요.
</Alert>
      }
    } catch (error) {
      <Alert severity="error" onClose={() => {}}>
  <AlertTitle>에러 발생</AlertTitle>
</Alert>
    }
   
  }


  return (
    <ThemeProvider theme={theme}>
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', flex: 1, justifyContent: 'center' }}>
          <img
            src="/images/SF-DB.png"
            alt="SF-DB 로고"
            style={{ width: '180px', height: '180px' }}
          />
        </Box>
              <Typography variant="h5" gutterBottom marked="center" align="center" sx={{ fontWeight: 'bold' }}>
            회원 로그인
          </Typography>
          <Typography variant="body2" align="center">
            <Link href="/register" underline="always">
              아직 계정이 없으신가요?
            </Link>
          </Typography>
        <Boxs component="form" noValidate sx={{ mt: 3 }} onSubmit={handleLogin}>
          <FormControl component="fieldset" variant="standard">
            
            <Grid container spacing={2}>

{/* 아이디 입력 */}                  
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="id"
                  name="id"
                  label="아이디"
                  value={id}
                  onChange={(e) => {
                    const value = e.target.value;
                    setId(value);
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  type="password"
                  id="pw"
                  name="pw"
                  label="비밀번호"
                  value={pw}
                  onChange={(e) => {
                    const value = e.target.value;
                    setPw(value);
                  }}
                />
              </Grid>
        </Grid>

            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2, bgcolor: 'primary.main' }}
              size="large"
            >
              로그인
            </Button>

             {/* 구글 로그인 버튼 */}
          <GoogleLogin
            clientId="YOUR_GOOGLE_CLIENT_ID" // 자신의 구글 OAuth 클라이언트 ID로 대체해야 합니다.
            onSuccess={handleGoogleLoginSuccess}
            onFailure={handleGoogleLoginFailure}
            cookiePolicy="single_host_origin"
            render={(renderProps) => (
              <Button
                fullWidth
                variant="contained"
                onClick={renderProps.onClick}
                disabled={renderProps.disabled}
                // 원하는대로 구글 로그인 버튼의 스타일을 설정합니다.
                sx={{ mt: 3, mb: 2, bgcolor: 'red', color: 'white' }}
                size='large'
              >
                Google 로그인
              </Button>
            )}
          />

       {/* 아이디 찾기와 비밀번호 찾기를 가로로 정렬 */}
       <Grid container justifyContent="center" spacing={2} sx={{ mt: 1 }}>
    <Grid item>
      <Typography variant="body2">
        <Link href="/findid" underline="always">
          아이디 찾기
        </Link>
      </Typography>
    </Grid>
    <Grid item>
      <Typography variant="body2">
        <Link href="/findpw" underline="always">
          비밀번호 찾기
        </Link>
      </Typography>
    </Grid>
  </Grid>

</FormControl>
          <FormHelperTexts></FormHelperTexts>
          </Boxs>
      </Box>
    </Container>
  </ThemeProvider>
  )
}
export default Login
