import React, { useState } from 'react';
import { Button, CssBaseline, TextField, FormControl, FormHelperText, Grid, Box, Typography, Container, Link, Snackbar, Alert} from '@mui/material/';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router';
import { styled } from 'styled-components';
import { userInfoDB, userLoginDB } from '../../axios/user/loginLogic';
import { serialize } from 'cookie';
import GoogleLogin from './GoogleLogin';
import KakaoLogin from './KakaoLogin';
import NaverLogin from './NaverLogin';
import { useSnackbar } from 'notistack';
import { getCookie } from '../../utils/getCookies';


const FormHelperTexts = styled(FormHelperText)`
  width: 100%;
  padding-left: 16px;
  font-weight: 700 !important;
  color: #d32f2f !important;
`;

const Boxs = styled(Box)`
  padding-bottom: 40px !important;
`;


const Login = () => {
  const theme = createTheme();
  const navigate = useNavigate();
  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [alertOn, setAlertOn] = useState(false);
  const [open, setOpen] = useState(false);
  const [emailError, setEmailError] = useState('');
  const emailRegex = /([\w-.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/;


  const { enqueueSnackbar } = useSnackbar(); 
  const handleClose = () => {
    setAlertOn(false)
  }

  {/* 사이트 자체 로그인 */}
  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const loginData = {
        userId: id,
        userPw: pw,
      };
      const res = await userLoginDB(loginData);
      console.log('로그인 결과:', res);
      if (res === 1) {
        const userInfoRes = await userInfoDB(loginData.userId)
        console.log(userInfoRes);
        const userProfileImageValue = userInfoRes[0].userProfileImage || '';
        document.cookie = serialize('userId', userInfoRes[0].userId, { path: '/' });
        document.cookie = serialize('userName', userInfoRes[0].userName , { path: '/' });
        document.cookie = serialize('userBirth', userInfoRes[0].userBirth , { path: '/' });
        document.cookie = serialize('userEmail', userInfoRes[0].userEmail , { path: '/' });
        document.cookie = serialize('userProfileImage', userProfileImageValue , { path: '/' });
        navigate('/main');
        const userName = getCookie('userName')
        enqueueSnackbar(`${userName}님, 어서오세요.`, { variant: 'success' });
        setAlertOn(true);
      } else {
        console.log('로그인 정보 재확인 필요');
        enqueueSnackbar('로그인에 실패했습니다.', { variant: 'warning' });
        setAlertOn(true);
      }
    } catch (error) {
      console.log(error);
      enqueueSnackbar('네트워크 오류 발생!', { variant: 'error' });
      setAlertOn(true);
    }
  };



  /* Style */
  const modalStyle = {
    position: 'absolute', top: '50%',left: '50%',
    transform: 'translate(-50%, -50%)', width: 450, height: 400,
    bgcolor: 'background.paper', borderRadius: '10px', boxShadow: 24, p: 4,
    display: 'flex', flexDirection: 'column', justifyContent: 'space-between'
  };
  

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Box sx={{ display: 'flex', alignItems: 'center', flex: 1, justifyContent: 'center' }}>
            <img
              src="/images/SF-DB.png"
              alt="SF-DB 로고"
              style={{ width: '180px', height: '180px' }}
            />
          </Box>
          <Typography variant="h5" gutterBottom marked="center" align="center" sx={{ fontWeight: 'bold' }}>
            회원 로그인
          </Typography>
          <Typography variant="body2" align="center">
            <Link href="/register" underline="always">
              아직 계정이 없으신가요?
            </Link>
          </Typography>
          <Boxs component="form" noValidate sx={{ mt: 3 }} onSubmit={handleLogin}>
            <FormControl component="fieldset" variant="standard">
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="id"
                    name="id"
                    label="아이디"
                    value={id}
                    onChange={(e) => {
                      const value = e.target.value;
                      setId(value);
                    }}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    type="password"
                    id="pw"
                    name="pw"
                    label="비밀번호"
                    value={pw}
                    onChange={(e) => {
                      const value = e.target.value;
                      setPw(value);
                    }}
                  />
                </Grid>
              </Grid>

              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2, bgcolor: 'primary.main' }}
                size="large"
              >
                로그인
              </Button>
   {/* 아이디 찾기와 비밀번호 찾기를 가로로 정렬 */}
   <Grid container justifyContent="center" spacing={2} sx={{ mt: 1 , marginTop:'-20px', marginBottom:'10px'}}>
                <Grid item>
                  <Typography variant="body2">
                    <Button variant='text'>
                      아이디 찾기
                    </Button>
                  </Typography>
                </Grid>
                <Grid item>
                  <Typography variant="body2">
                  <Button variant='text'>
                      비밀번호 찾기
                    </Button>
                  </Typography>
                </Grid>
              </Grid>

      {/* 소셜로그인 */}
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '10px', width: '280px', margin: 'auto' }}>
            {/*구글*/}
            <GoogleLogin />
            {/*네이버*/}
              <NaverLogin/>
            {/*카카오*/}
            <KakaoLogin/>
          </div>

           
            </FormControl>
            <FormHelperTexts></FormHelperTexts>
          </Boxs>
        </Box>

        <Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={modalStyle}>
  <IconButton
          style={{ position: 'absolute', top: '25px', right: '20px' }}
          onClick={handleClose}
        >
          <CancelIcon />
        </IconButton>
        <div style={{display:'flex'}}>
    <Typography id="modal-modal-title" variant="h6" component="h2">
     {releaseSoonMovies.title}
    </Typography>
    <Typography id="modal-modal-title" variant="h6" component="h2" style={{fontSize:'12px' , marginTop:'6px',marginLeft:'5px'}}>
     {releaseSoonMovies.titleOrg}
    </Typography>
        </div>

    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ width: '250px', height: '300px', marginTop:'5px' , marginBottom: '30px'}}>
          <img src={posterUrls} style={{  width: '100%', height: '320px', borderRadius: '3px 3px 3px 3px' }}/>
        </div> 
{/* 이메일 설정 */}
<div style={{display:'flex', flexDirection:'column' }}>
  <div>
  <Typography variant='overline' style={{fontSize:'15px',marginRight:'5px'}}>· 이메일 : </Typography>
  <TextField
  hiddenLabel
  defaultValue={emailValue}
  id="userEmail"
  error={emailError !== ''}
  helperText={emailError}
  variant="standard"
  onChange={(event) => {
    setEmailValue(event.target.value)
  }}/>
  <Button 
  aria-owns={popoverOpen ? 'mouse-over-popover' : undefined}
  aria-haspopup="true"
  onMouseEnter={handlePopoverOpen}
  onMouseLeave={handlePopoverClose}
  color="science"
  disabled={isNoticeTaken}
   variant="contained" style={{color:'white',fontSize:'10px' , marginLeft:'15px' , marginBottom:'3px'}} onClick={handleReleaseNotice}>알림 받기</Button>
  </div>
</div>
</div>  
  </Box>
</Modal>

        <Snackbar open={alertOn} autoHideDuration={3000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
            로그인에 성공했습니다!
          </Alert>
        </Snackbar>
      </Container>
    </ThemeProvider>
  );
};

export default Login;
