import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router';
import { BoxOfficeDetailDB, movieDetailDB, ottExistanceDB } from '../../axios/main/movieLogic';
import { firebaseStorage } from '../../utils/firebase';
import HeaderBar from '../../components/HeaderBar';
import Footer from '../../components/Footer';
import { MovieProvider } from '../../utils/movieDetailContext';
import BoxOfficeDetailTopSection from './BoxOfficeDetailTopSection';
import BoxOfficeDetailMidSection from './BoxOfficeDetailMidSection';
import BoxOfficeDetailMidSection2 from './BoxOfficeDetailMidSection2';
import BoxOfficeDetailBtmSection from './BoxOfficeDetailBtmSection';
import BoxOfficeDetailBtmSection2 from './BoxOfficeDetailBtmSection2';




const BoxOfficeDetailPage = () => {
  const location = useLocation();
  const movieInfo = location.pathname.split('/boxofficeDetail/')[1];
  const [movieId, movieSeq] = movieInfo.match(/([A-Z])(\d+)/).slice(1);
  const [movieDetail , setMovieDetail] = useState([])
  const [posterUrl , setPosterUrl] = useState('');
  const [ottInfo , setOttInfo] = useState({}) 


  useEffect(() => {
    const getMovieDetail = async () => {
      try {
        const res = await BoxOfficeDetailDB(movieId, movieSeq);
        setMovieDetail(res[0]);
        setPosterUrl(res[0].poster);

        // 포스터 URL을 Firebase Storage에 업로드
        const uploadedUrl = await uploadPoster(movieId, movieSeq, res[0].poster);
        console.log('업로드된 Poster URL:', uploadedUrl);
      } catch (error) {
        console.log("영화 상세정보 로드 실패:", error);
      }
    };

    getMovieDetail();
  }, [movieId, movieSeq]);

  const uploadPoster = async (movieId, movieSeq, posterUrl) => {
    const storageRef = firebaseStorage.ref(`poster/${movieId}${movieSeq}.jpg`);
    try {

    // 포스터 URL을 사용하여 이미지 파일을 다운로드
    const response = await fetch(posterUrl);
    const blob = await response.blob();

    // Blob 객체를 Firebase Storage에 업로드
    await storageRef.put(blob);
      // 포스터를 Firebase Storage에 업로드
      await storageRef.putString(posterUrl, 'data_url'); // 'data_url' 형식으로 업로드
      // 업로드가 완료되면 포스터 URL을 반환
      const url = await storageRef.getDownloadURL();
      return url;
    } catch (error) {
      console.error("포스터 업로드 에러:", error);
      return null;
    }
  };


       /* OTT */
       useEffect(()=> {
        const getOttExistance = async () => {
        try {
          const res = await ottExistanceDB(movieSeq)
          setOttInfo(res[0])
          console.log(ottInfo);
        } catch (error) {
          console.log("OTT 정보 로드 실패 : ", error);
        }
        }
      getOttExistance()
        },[])



  return (
    <>
<MovieProvider>
<HeaderBar/>

  {/* Top Section */}
    <div style={{marginTop:'70px' , marginLeft:'-10px'}}>
    <BoxOfficeDetailTopSection movieDetail={movieDetail}/>
    </div>
  
  {/* Mid Section */}
  <div>
    <BoxOfficeDetailMidSection movieDetail={movieDetail} posterUrl={posterUrl} ottInfo={ottInfo}/>
  </div>
  <div>
    <BoxOfficeDetailMidSection2 movieDetail={movieDetail} />
  </div>

{/* Btm Section */}
<div style={{marginTop:'100px'}}>
    <BoxOfficeDetailBtmSection movieDetail={movieDetail}/>
  </div>
<div style={{marginTop:'100px'}}>
<BoxOfficeDetailBtmSection2 movieDetail={movieDetail}/>
</div>
<Footer/>
</MovieProvider>
    </>
  )
}

export default BoxOfficeDetailPage
