import { Avatar, Typography } from '@mui/material';
import React from 'react';

const MovieDetailMidSection2 = ({ movieDetail }) => {

  const midSection2Style = {
    marginLeft: '80px',
    marginTop: '50px',
    maxWidth: '1000px',
    minHeight: '300px',
    maxHeight: '400px',
    display: 'flex', 
  }

  const staffRoleStyle = {
    marginTop: '80px',
    display: 'flex', // Use flex display to horizontally align items
    alignItems: 'center', // Center items vertically
    position: 'relative'
  }

  const avatarStyle = {
    marginTop: '5px',
    marginRight: '20px'
  }

  const directorStyle = {
    
    marginLeft : '-120px',
    maxWidth : '300px',
    display: 'flex',
  }

  return (
    <>
      <div style={midSection2Style}>
<Typography variant='h4' style={{ fontSize: '25px' }}>출연 / 제작</Typography>
        <div style={staffRoleStyle}>
        
        

          {/* 감독 */}
          <div style={directorStyle}>
          <Avatar style={avatarStyle} />
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start'}}>
            <Typography variant='subtitle2' style={{ fontSize: '16px' }}>{movieDetail.directorNms}</Typography>
            <div style={{ marginTop: '5px' }}><Typography variant='button' style={{ opacity: '60%', fontSize: '15px' }}>감독</Typography></div>
          </div>
          </div>
         {/* 밑줄 */} <div style={{  position: 'absolute', // Position absolute to start from the right place
    left: 0,
    width: '100%',borderBottom: '1px solid black',opacity:'10%',marginTop: '10px',}}></div>
          {/* 감독 */}
         




        </div>
      </div>
    </>
  );
};

export default MovieDetailMidSection2;
