import React from 'react';

const MovieDetailTopSection = ({ movieDetail, imageUrl }) => {
  const topSectionStyle = {
    width: '100%',
    height: '400px',
    position: 'relative',
  };

  const imageStyle = {
    display: 'block',
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  };

  const overlayStyle = {
    position: 'absolute',
    bottom: 0,
    left: 0,
    width: '100%',
    height: '50%', // Adjust the height of the gradient overlay
    padding: '20px',
    background: 'linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.9))', // Adjust the opacity here
    color: 'white',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'flex-end', // Align text to bottom
  };

  const titleStyle = {
    marginBottom: '10px',
  };

  return (
    <div style={topSectionStyle}>
      <img src={imageUrl} alt={movieDetail.title} style={imageStyle} />
      <div style={overlayStyle}>
        <div>
          <h1 style={titleStyle}>{movieDetail.title}</h1>
          {/* Additional information can be added here */}
        </div>
      </div>
    </div>
  );
};

export default MovieDetailTopSection;
