import React from 'react';
import { Typography } from '@mui/material';

const MovieDetailMidSection = ({ movieDetail, posterUrl }) => {
  const midSectionStyle = {
    width: '1525px',
    height: '1200px',
    backgroundColor: '#f5f5f5',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  const contentContainer = {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    width: '90%',
  };

  const contentStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom : '650px',
    display: 'flex',
    flexDirection: 'column', // Changed to column layout
    alignItems: 'flex-start', // Align content to start
  };

  const posterStyle = {
    width: '300px',
    height: '450px',
    objectFit: 'cover',
    marginRight: '50px',
  };
 
  const ottInfoStyle = {
      width: '100px', // Fixed width for ottInfoStyle
      textDecoration: 'underline', // Add underline to ottInfoStyle
      marginBottom: '10px', // Added marginBottom for spacing
    marginBottom: '10px', 
  }

  const plotTextStyle = {
    fontSize: '15px',
    opacity: '80%',
    marginTop : 'auto'
  };

  return (
    <div style={midSectionStyle}>
      <div style={contentContainer}>
        <div style={contentStyle}>
          <img src={posterUrl} alt={movieDetail.title} style={posterStyle} />
        </div>
        <div style={{ flex: 1 }}>
        <div variant="body1" style={ottInfoStyle} >
           <div>
           
            </div>OTT 유무
          </div>
          <Typography variant="button" style={plotTextStyle}>
            {movieDetail.plotText}
          </Typography>
        </div>
      </div>
    </div>
  );
};

export default MovieDetailMidSection;
