import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css'; // slick 라이브러리의 스타일을 가져옵니다
import 'slick-carousel/slick/slick-theme.css'; // slick 라이브러리의 테마 스타일을 가져옵니다
import axios from 'axios';
import { top20sfmoviesDB } from '../../axios/main/movieLogic';

const MainTopSection = () => {
  const [top20Movies, setTop20Movies] = useState([]);

  useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
        const res = await top20sfmoviesDB();
        setTop20Movies(res);
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    };

    getTop20SfMovies();
  }, []);

  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2
        }
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1
        }
      }
    ]
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h2>Top 20 Science Fiction Movies</h2>
      <Slider {...settings}>
        {top20Movies.map((movie, index) => (
          <div key={index}>
            <img
              src={movie.poster}
              alt={movie.title}
              style={{ width: '200px', height: 'auto' }}
            />
            <p style={{ marginTop: '5px', fontSize: '14px' }}>{movie.title}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default MainTopSection;
