    import React, { useState , useEffect }  from 'react'
    import axios from 'axios';
    import { top20sfmoviesDB } from '../../axios/main/movieLogic';


    const MainTopSection = () => {
      
      const [top20Movies, setTop20Movies] = useState([]);
      const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
      const res = await top20sfmoviesDB()
      console.log(res);
      setTop20Movies(res)
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    }
        getTop20SfMovies()
      }, []);

      
      const handleNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % top20Movies.length);
      };

      const handlePrevious = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + top20Movies.length) % top20Movies.length);
      };

      return (
        <>
  <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h2>Top 20 Science Fiction Movies</h2>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
          <button onClick={handlePrevious}>Previous</button>
          <div style={{ display: 'flex', overflow: 'hidden', marginRight: '-20px' }}>
            {top20Movies.map((movie, index) => (
              <div
                key={index}
                style={{
                  flex: '0 0 auto',
                  marginRight: '10px',
                  textAlign: 'center',
                  transform: `translateX(${-(currentIndex - index) * 100}%)`,
                  transition: 'transform 0.3s ease-in-out', // 슬라이딩 애니메이션 추가
                }}
              >
                <img
                  src={movie.poster}
                  alt={movie.title}
                  style={{ maxWidth: '200px', height: 'auto' }}
                />
                <p style={{ marginTop: '5px', fontSize: '14px' }}>{movie.title}</p>
              </div>
            ))}
          </div>
          <button onClick={handleNext}>Next</button>
        </div>
      </div>
    </div>
        </>
      )
    }

    export default MainTopSection
