import React from 'react'

const MovieDetailMidSection = ({ movieDetail , posterUrl }) => {
  const midSectionStyle = {
    width: '1525px',
    height:'1000px',
    backgroundColor: '#f5f5f5'
  }



  const contentStyle = {
    display: 'flex',
    alignItems: 'center',
  };

  const posterStyle = {
    width: '300px',
    height: '450px',
    objectFit: 'cover',
    marginRight: '30px',
  };

  const plotTextStyle = {
    fontSize: '18px',
  };

  return (
    <>
  <div style={midSectionStyle}>
      <div style={contentStyle}>
        <img src={posterUrl} alt={movieDetail.title} style={posterStyle} />
        <div>
          <Typography variant="subtitle2" style={plotTextStyle}>{movieDetail.plotText}</Typography>
        </div>
      </div>
    </div>
    </>
  )
}

export default MovieDetailMidSection
