import React from 'react';
import { Carousel, Card, CardContent, Grid, Typography } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const MovieCarousel = ({ top20Movies }) => {
  return (
    <Carousel>
      {top20Movies.map((movie, index) => (
        <Carousel.Item key={index}>
          <Card style={{ maxWidth: 250, height: 430, mx: 2, border: 'none', margin: '0 auto' }}>
            <Card.Img variant="top" src={movie.poster} alt={movie.title} style={{ height: '350px', borderRadius: '3px 3px 0 0' }} />
            <Card.Body>
              <Typography variant="subtitle2" style={{ fontSize: 15 }}>{movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}</Typography>
              <Grid container justifyContent="flex-start">
                <Typography variant="body2">{movie.prodYear} · {movie.nation}</Typography>
              </Grid>
            </Card.Body>
          </Card>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default MovieCarousel;
