import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { top20sfmoviesDB } from '../../axios/main/movieLogic';
import Slider from 'react-slick';

const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 5,
  slidesToScroll: 5,
  initialSlide: 0,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3,
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2,
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      }
    }
  ]
};

const MainTopSection = () => {



  const [top20Movies, setTop20Movies] = useState([]);

  useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
        const res = await top20sfmoviesDB();
        setTop20Movies(res);
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    };

    getTop20SfMovies();
  }, []);

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
    <h2>Top 20 Science Fiction Movies</h2>
    <div
      style={{
        display: 'flex',
        flexWrap: 'nowrap',
        overflowX: 'auto',
        scrollBehavior: 'smooth',
        padding: '10px',
      }}
    >
      <Slider {...settings}>
        {top20Movies.map((movie, index) => (
          <div
            key={index}
            style={{
              flex: '0 0 auto',
              marginRight: '10px',
              textAlign: 'center',
            }}
          >
            <img
              src={movie.poster}
              alt={movie.title}
              style={{ width: '200px', height: 'auto' }}
            />
            <p style={{ marginTop: '5px', fontSize: '14px' }}>{movie.title}</p>
          </div>
        ))}
      </Slider>
    </div>
  </div>
  );
};

export default MainTopSection;
