import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { top20sfmoviesDB } from '../../axios/main/movieLogic';
import Slider from 'react-slick';

const MainTopSection = () => {
  const [top20Movies, setTop20Movies] = useState([]);

  useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
        const res = await top20sfmoviesDB();
        setTop20Movies(res);
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    };

    getTop20SfMovies();
  }, []);




  
  const settings = {
    dots: true,
    arrows : true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5
  };





  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
    <h2>Top 20 Science Fiction Movies</h2>
    <Slider {...settings}> {/* Slider 컴포넌트에 settings 설정을 전달합니다 */}
      {top20Movies.map((movie, index) => (
        <div key={index}>
          <img
            src={movie.poster}
            alt={movie.title}
            style={{ width: '200px', height: 'auto' }}
          />
          <p style={{ marginTop: '5px', fontSize: '14px' }}>{movie.title}</p>
        </div>
      ))}
    </Slider>
  </div>
  );
};

export default MainTopSection;
