import React from 'react';
import { useEffect } from 'react';

const BoxOfficeDetailTopSection = ({ movieDetail }) => {
  const title = movieDetail.title
  const titleOrg = movieDetail.titleOrg
  const prodYear = movieDetail.prodYear
  const [imageUrl , setImageUrl] = useState('')
  useEffect(() => {
    const searchMovieBackground = async () => {
      try{
        const query = titleOrg + prodYear + ' stills'
        const bgImage = await getGoogleImageApi(query)
        setImageUrl(bgImage.link)
      }catch(error){
        console.log("영화 배경스틸 검색 실패 : ", error);
      }
    }
    if (title !== undefined && prodYear !== undefined) {
      searchMovieBackground()
    }
  },[title, prodYear]);



  const formatRuntime = (runtime) => {
    const hours = Math.floor(runtime / 60); // 시간
    const minutes = runtime % 60; // 분
    return `${hours}시간 ${minutes}분`;
  };
  
    //관객수 표기
    const formatAudience = (audience) => {
      if (audience >= 10000) {
        const tenThousand = Math.floor(audience / 10000);
          return `${tenThousand}만 명`;
      } else {
        return audience.toLocaleString() + ' 명';
      }
    };

  /*  Style  */
  const topSectionStyle = {
    width: '1525px',
    height: '500px', // 고정된 높이
    overflow: 'hidden',
    position: 'relative',
  };

  const imageStyle = {
    display: 'block',
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    objectPosition: 'center 35%'
  };

  const overlayStyle = {
    position: 'absolute',
    bottom: 0,
    left: 0,
    width: '100%',
    height: '50%', // 높이 조정
    padding: '20px',
    background: 'linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.9))',
    color: 'white',
    fontWeight: 'bold',
    display: 'flex',
    flexDirection: 'column', // 수직으로 정렬
    justifyContent: 'space-between', // 상하 공간 분배
  };

  const infoStyle = {
    display: 'flex',
    flexDirection: 'column', // 수직으로 정렬
    marginLeft: '40px', 
  };

  const titleStyle = {
    marginTop:'90px',
    marginBottom: '5px', // 간격 조절
    marginLeft: '75px', 
    fontSize: '40px',
  };

  const titleOrgStyle = {
    fontSize: '12px',
    marginLeft: '80px', 
  };
  
  const yearNationStyle = {
    fontSize: '15px',
    marginTop: '12px',
    marginLeft: '40px'
  };

  const etcInfoStyle = {
    fontSize: '15px',
    marginTop: '10px',
    marginLeft: '40px'
  }

  return (
    <div style={topSectionStyle}>
      <img src={imageUrl} alt="Movie Stills" style={imageStyle} />
      <div style={overlayStyle}>
        <div>
          <h1 style={titleStyle}>{movieDetail.title}</h1>
          <span style={titleOrgStyle}>{movieDetail.titleOrg}</span>
          <div style={infoStyle}>
            <span style={yearNationStyle}>{movieDetail.prodYear}  ·  {movieDetail.nation}</span>
            <span style={etcInfoStyle}> {formatRuntime(parseInt(movieDetail.runtime))} · {movieDetail.rating}</span>
            <span style={etcInfoStyle}> 예매율 {movieDetail.rating}% · {formatRuntime(parseInt(movieDetail.audiAcc))}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BoxOfficeDetailTopSection;
