import { Typography } from '@mui/material'
import React, { useEffect, useState } from 'react'
import { getMovieCommentDB } from '../../axios/detail/contentsLogic';

const MovieDetailBtmSection = ({ movieDetail }) => {
  const movieId = movieDetail.movieId;
  const movieSeq = movieDetail.movieSeq;
  const [movieCommentList , setMovieCommentList] = useState([])

  useEffect(() => {
  const getMovieComment = async () => {
    try {
     const res = await getMovieCommentDB(movieId , movieSeq)
     setMovieCommentList(res)
    } catch (error) {
      console.log("영화 코멘트 로드 실패 : " , error);
    }
  }
  getMovieComment()
  },[])


  {/* Style */}
  const btmSectionStyle = {
    marginLeft: '100px',
    marginTop: '30px',
    maxWidth: '1500px',
    minHeight: '750px',
    maxHeight: '1600px',
  } 

  const commentListStyle = {
    width :'300px',
    height : '500px',
  }
   
  return (
    <>

    <div style={btmSectionStyle}>
      <Typography variant="h4" style={{ fontSize: '25px' }}>
          코멘트
        </Typography>

        <div style={commentListStyle}>
      <Slider {...settings}>
      {top20Movies.map((movie, index) => (
        <div key={index} style={{ position: 'relative'  }}>
          <Card sx={{ maxWidth: 250, height: 450 ,mx: 2 , border:'none',margin: '0 auto' ,cursor: 'pointer' }}
            onClick={() => navigate(`/movieDetail/${movie.movieId}${movie.movieSeq}`)}>

            <img src={posterUrls[index]} alt={movie.title} style={{ width: '100%', height: '350px' , borderRadius:'3px 3px 3px 3px' }} />
            
            <CardContent style={{ border:'none', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start' }}>
          
              <Typography variant="subtitle2" sx={{fontSize:15}}>{movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}</Typography>
              <Grid container justifyContent="flex-start" style={{marginTop:'5px'}}>
                <Typography variant="body2">{movie.prodYear} · {movie.nation}</Typography>
              </Grid>
          
              <Typography variant="caption" style={{marginTop:'5px'}}> {movie.directorNms}
              </Typography>
            </CardContent>
          
          </Card>
        </div>
      ))}
    </Slider>
        </div>
    </div>




    </>
  )
}

export default MovieDetailBtmSection
