import React, { useEffect, useState } from "react";
import config from "../../config";
import { naverSocialLogin } from "../../axios/user/loginLogic";

const NaverLogin = () => {
  const [loginAlert, setLoginAlert] = useState(null);

  const initializeNaverLogin = () => {
    const naverLogin = new window.naver.LoginWithNaverId({
      clientId: config.naverClientId,
      callbackUrl: config.naverRedirectUri,
      clientSecret: config.naverClientSecret,
      isPopup: true,
      loginButton:{color: 'green', type: 3, height: '50'}
    });

       // 로그인 성공 시
       naverLogin.getLoginStatus(async (status) => {
        if (status) {
          const userData = naverLogin.user;

          
          // 이제 Axios를 사용하여 사용자 데이터를 백엔드로 보낼 수 있습니다
          const res = await naverSocialLogin(userData)
          axios.post("/api/naverSocialLogin", userData)
            .then((response) => {
              // 백엔드로부터의 응답을 처리하려면 이 곳에서 처리하시면 됩니다
              console.log("사용자 데이터가 성공적으로 백엔드로 전송되었습니다:", response.data);
            })
            .catch((error) => {
              // Axios 요청 중 발생한 오류를 처리하려면 이 곳에서 처리하시면 됩니다
              console.error("사용자 데이터를 백엔드로 전송하는 중 오류가 발생했습니다:", error);
            });
        }
      });
      naverLogin.init();
    }

  useEffect(() => {
    // 네이버 SDK 스크립트 로드
    const script = document.createElement("script");
    script.src = "https://static.nid.naver.com/js/naveridlogin_js_sdk_2.0.2.js";
    script.charset = "utf-8";
    script.onload = () => {
      initializeNaverLogin();
    };
    document.body.appendChild(script);
  }, []);

  return (
    <div id="naverIdLogin" style={{ textAlign: 'center' }}>
  </div>
  );
};

export default NaverLogin;
