import React, { useEffect, useState } from 'react'
import { todayBoxofficeDB } from '../../axios/main/movieLogic'
import { Card, CardContent, Grid, IconButton, Tooltip, Typography } from '@mui/material'
import Slider from 'react-slick'

const MainBtmSection = () => {
  const [todayBoxofficeRank , setTodayBoxofficeRank] = useState([])

  useEffect(() => {
    const getTodayBoxofficeRank = async() => {
      try {
      const res = await todayBoxofficeDB()
      setTodayBoxofficeRank(res)  
      } catch (error) {
        console.log('박스오피스 정보 로드 실패 : ' , error);
      }
      getTodayBoxofficeRank()
    }
  },[])
  
  const settings = {
    dots: true,
    arrows : true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5
  };

  
  return (
    <>
 <> 
    <div>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: '16px' , marginRight:'1250px' }}>
        <Typography variant="h6" sx={{ fontSize: 25 }}>추천 SF 영화</Typography>
        <Tooltip title="왓챠피디아 기준 평가 수 10만 이하의 작품 중 가장 높은 평균 별점을 받은 영화들입니다." placement="right">
          <IconButton>
            <InfoIcon />
          </IconButton>
        </Tooltip>
      </div>
    <Slider {...settings}>
      {todayBoxofficeRank.map((movie, index) => (
        <div key={index} style={{ position: 'relative' }}>
          <Card sx={{ maxWidth: 250, height: 430 ,mx: 2 , border:'none' , margin: '0 auto'}}>
            <img src={movie.poster} alt={movie.title} style={{ width: '100%', height: '350px' , borderRadius:'3px 3px 3px 3px' }} />
            <CardContent style={{ border:'none', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start' }}>
              <Typography variant="subtitle2" sx={{fontSize:15}}>{movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}</Typography>
              <Grid container justifyContent="flex-start">
                <Typography variant="body2">{movie.prodYear} · {movie.nation}</Typography>
              </Grid>
            </CardContent>
          </Card>
        </div>
      ))}
    </Slider>
  </div>
    </>
    </>
  )
}

export default MainBtmSection
