import * as React from 'react';
import Title from './Title';
import TheatersIcon from '@mui/icons-material/Theaters';
import { useEffect } from 'react';
import { releaseSoonMovieDB } from '../../axios/main/movieLogic';
import { useState } from 'react';
import { Button, Card, CardContent, Typography } from '@mui/material';
import { useSnackbar } from 'notistack';
import { useNavigate } from 'react-router';
import { firebaseStorage } from '../../utils/firebase';
import { getYouTubeVideoByQuery } from '../../utils/youtubeApi';
import { releaseNoticeEmail } from '../../axios/detail/contentsLogic';

export default function ReleaseSoon({ userInfo }) {
  const userId = userInfo.userId
  const userEmail = userInfo.userEmail
  const [releaseSoonMovies , setReleaseSoonMovies] = useState([])
  const [posterUrls, setPosterUrls] = useState();
  const [trailerId, setTrailerId] = useState(null);
  const [open, setOpen] = useState(false);
  const [alertOn, setAlertOn] = useState(false)
  const { enqueueSnackbar } = useSnackbar(); 

  useEffect(() => {
  const getReleaseSoonMovie = async() => {
    try {
      const res = await releaseSoonMovieDB()
      setReleaseSoonMovies(res[0])
    } catch (error) {
      console.log('개봉 예정 영화 로드 실패 : ', error);
    }
  }
  getReleaseSoonMovie()
  },[])

  useEffect(() => {
    const getPosterUrls = async () => {
        const storageRef = firebaseStorage.ref(`poster/${releaseSoonMovies.movieId}${releaseSoonMovies.movieSeq}.jpg`);
        try {
          const url = await storageRef.getDownloadURL();
          setPosterUrls(url);
        } catch (error) {
          console.log("포스터 URL 페칭 에러 : " , error);
          return null;
        }
    };
    getPosterUrls();
  }, [releaseSoonMovies]);

  useEffect(() => {
    const searchMovieTrailer = async () => {
      try {
        const query = releaseSoonMovies.title + releaseSoonMovies.prodYear + '예고편';
        console.log(query);
        const searchResults = await getYouTubeVideoByQuery(query)
        if(searchResults && searchResults.items && searchResults.items.length > 0) {
          const topVideo = searchResults.items[0];
          setTrailerId(topVideo.id.videoId)
        }
      } catch (error) {
        console.log('영화 예고편 검색 실패 : ', error);
      } 
    }
    if (releaseSoonMovies.title !== undefined && releaseSoonMovies.prodYear !== undefined) {
      searchMovieTrailer()
    }
  },[releaseSoonMovies.title, releaseSoonMovies.prodYear]);


  const handleNoticeClick = () => {
    setOpen(true)
  }
  const handleClose = () => setOpen(false); 
  const handleAlertClose = () => {
    setAlertOn(false)
  }

  {/* 알림 설정 */}
  const handleReleaseNotice = async() => {
    const requestData = {
      userId : userId,
      userEmail : userEmail,
      movieId : releaseSoonMovies.movieId,
      movieSeq : releaseSoonMovies.movieSeq,
      title : releaseSoonMovies.title,
      repRlsDate : releaseSoonMovies.repRlsDate,
      posterUrls : posterUrls
    }
    try {
      const res = await releaseNoticeEmail(requestData)
    } catch (error) {
      console.log('개봉 예정작 이메일 알림설정 실패 :' , error);
    }
  }



  /* Style */
  const releaseSoonSectiontyle = {
    minWidth: '600px',
    maxWidth: '650px',
    minHeight: '150px',
    marginTop: '10px',
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'row'
  };

  const videoStyle = {
    marginTop: '10px',
    marginBottom: '10px',
    marginLeft: '20px',
    width: '400px',
    minWidth: '300px',
    maxWidth: '400px',
    height: '250px',
    minHeight: '250px',
    maxHeight: '250px'
  } 

const modalStyle = {
  position: 'absolute', top: '50%',left: '50%',
  transform: 'translate(-50%, -50%)', width: 400, height: 300,
  bgcolor: 'background.paper', borderRadius: '10px', boxShadow: 24, p: 4,
  display: 'flex', flexDirection: 'column', justifyContent: 'space-between'
};

  return (
    <>
    <div style={{display:'flex'}}>
      <TheatersIcon style={{marginLeft:'15px',marginRight:'5px' ,marginTop:'3px'}}/><Title>개봉 예정</Title>
    <div style={{ marginLeft: '15px' }}>
          <Button color='science' variant='contained' style={{ fontSize: '10px' ,color:'white'}} >
            알림 설정
          </Button>
      </div>
    </div>


    <div style={releaseSoonSectiontyle}>
    <Card sx={{ maxWidth: 250, height: 250, mx: 2, border: 'none', marginRight: '10px'}}>
        <img
                src={posterUrls}
                alt={releaseSoonMovies.title}
                style={{ width: '100%', height: '250px', borderRadius: '3px 3px 3px 3px'}}
              />
            </Card>


   <div style={videoStyle}>
   {/* 동영상 삽입 */}
   {trailerId && (
            <iframe
              width="100%"
              height="100%"
              src={`https://www.youtube.com/embed/${trailerId}`}
              allowFullScreen
              style={{ border: 'none', borderRadius: '5px' }} 
            ></iframe>
          )}
   </div>

   <Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={modalStyle}>
  <IconButton
          style={{ position: 'absolute', top: '25px', right: '20px' }}
          onClick={handleClose}
        >
          <CancelIcon />
        </IconButton>
    <Typography id="modal-modal-title" variant="h6" component="h2">
     프로필 편집
    </Typography>

    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
    {/* 이미지 변경 */}
    <div style={{ width: '150px', height: '150px', borderRadius: '50%', overflow: 'hidden', marginBottom: '10px'}}>
          <img src={userProfileImage ? userProfileImage : '/images/astronaut.jpg'} style={{ width: '100%', height: '100%', objectFit: 'cover' }}/>
        </div>
        <Button color="science" variant="contained" onClick={handleImageChange} style={{ color: 'white',fontSize:'12px',marginBottom:'20px'}}>사진 편집</Button>

{/* 이름 변경 */}
<div style={{display:'flex', flexDirection:'column'}}>
  <div>
  <Typography variant='overline' style={{fontSize:'15px',marginRight:'5px'}}>· 이름 : </Typography>
  <TextField
  hiddenLabel
  id="changeUserName"
  defaultValue={userNameValue}
  variant="standard"
  onChange={(event) => {
  setUserNameValue(event.target.value)
  setIsNameChanged(event.target.value !== userNameValue);
  }}
/>
  <Button color="science" variant="text" disabled={!isNameChanged} style={{fontSize:'12px' , marginLeft:'15px' , marginBottom:'0px'}} onClick={handleNameChange}>변경하기</Button>
  </div>
</div>
</div>  
  </Box>
</Modal>

    </div>
    </>
  );
}
