import * as React from 'react';
import Title from './Title';
import TheatersIcon from '@mui/icons-material/Theaters';
import { useEffect } from 'react';
import { releaseSoonMovieDB } from '../../axios/main/movieLogic';
import { useState } from 'react';
import { Card } from '@mui/material';
import { useSnackbar } from 'notistack';
import { useNavigate } from 'react-router';

export default function ReleaseSoon({ userInfo }) {
  const navigate = useNavigate();
  const [releaseSoonMovies , setReleaseSoonMovies] = useState([])
  const [posterUrls, setPosterUrls] = useState([]);
  const [alertOn, setAlertOn] = useState(false)
  const { enqueueSnackbar } = useSnackbar(); 

  useEffect(() => {
  const getReleaseSoonMovie = async() => {
    try {
      const res = await releaseSoonMovieDB()
      setReleaseSoonMovies(res)
    } catch (error) {
      console.log('개봉 예정 영화 로드 실패 : ', error);
    }
  }
  getReleaseSoonMovie()
  },[])

  useEffect(() => {
    const getPosterUrls = async () => {
      const urls = await Promise.all(userMovieArchive.map(async (movie) => {
        const storageRef = firebaseStorage.ref(`poster/${movie.movieId}${movie.movieSeq}.jpg`);
        try {
          const url = await storageRef.getDownloadURL();
          return url;
        } catch (error) {
          console.log("포스터 URL 페칭 에러 : " , error);
          return null;
        }
      }));
      setPosterUrls(urls);
    };

    getPosterUrls();
  }, [userMovieArchive]);


  /* Style */
  const releaseSoonSectiontyle = {
    minWidth: '600px',
    maxWidth: '600px',
    minHeight: '150px',
    marginTop: '20px',
    display: 'flex',
    flexDirection: 'row'
  };

/*  
> */
  return (
    <>
    <div style={{display:'flex'}}>
      <TheatersIcon style={{marginLeft:'15px',marginRight:'5px' ,marginTop:'3px'}}/><Title>개봉 예정</Title>
    </div>

    <div style={releaseSoonSectiontyle}>
    {releaseSoonMovies.map((movie, index) => (
          <div key={index}>
    <Card sx={{ maxWidth: 240, height: 430, mx: 2, border: 'none', marginRight: '10px', cursor: 'pointer'}}>
        <img
                src={posterUrls[index]}
                alt={movie.title}
                style={{ width: '100%', height: '350px', borderRadius: '3px 3px 3px 3px' ,cursor:'pointer'}}
                onClick={() => navigate(`/movieDetail/${movie.movieId}${movie.movieSeq}`)}
              />
              <CardContent style={{ border: 'none', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start'}}>
                <Typography variant="subtitle2" sx={{ fontSize: 15 }}>
                  {movie.title.length > 15 ? `${movie.title.slice(0, 15)}...` : movie.title}
                </Typography>
                <FormControlLabel
                  label={<Typography variant="overline" style={{ fontSize: '14px' }}>{movie.addDate} 추가됨</Typography>}
                  control={<Checkbox checked={checked[index] || false} onChange={() => handleToggle(index)} />}
                />
              </CardContent>
            </Card>
          </div>
        ))}
    </div>
    </>
  );
}
