  import React, { useEffect, useState } from 'react';
  import { Typography } from '@mui/material';
  import { ottExistanceDB } from '../../axios/main/movieLogic';

  const MovieDetailMidSection = ({ movieDetail, posterUrl }) => {
    const movieSeq = movieDetail.movieSeq;
    const [ottInfo , setOttInfo] = useState({}) 
    
    /* OTT */
    useEffect(()=> {
    const getOttExistance = async () => {
    try {
      const res = await ottExistanceDB(movieSeq)
      setOttInfo(res[0])
      console.log(ottInfo);
    } catch (error) {
      console.log("OTT 정보 로드 실패 : ", error);
    }
    }
  getOttExistance()
    },[])

    const ottPlatforms = Object.keys(ottInfo).filter(
      (platform) => platform !== "movieSeq" && platform !== "title" && ottInfo[platform]
    );



    const midSectionStyle = {
      width: '1525px',
      height: '1200px',
      backgroundColor: '#f5f5f5',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    };

    const contentContainer = {
      display: 'flex',
      alignItems: 'flex-start',
      justifyContent: 'space-between',
      width: '90%',
    };

    const contentStyle = {
      display: 'flex',
      marginBottom : '650px',
      flexDirection: 'column', 
      alignItems: 'flex-start',
    };

    const posterStyle = {
      width: '300px',
      height: '450px',
      objectFit: 'cover',
      marginRight: '50px',
    };
  
    const ottInfoStyle = {
      display: 'flex',
      alignItems: 'center',
      width: '1000px',
      marginTop : '50px',
      marginBottom: '50px',
    };

    const ottUnderline = {
      width: '1000px',
      borderBottom: '1px solid black',
      opacity:'10%',
      marginTop :' 50px',
      marginBottom: '50px',
    };


    const plotTextStyle = {
      fontSize: '15px',
      opacity: '80%',
      marginTop : 'auto'
    };

    const ottLogoStyle = {
      width: '50px',
      height: '50px',
      borderRadius: '50%', // Make the image circular
      objectFit: 'cover', // Preserve the image aspect ratio
      border: '2px solid white', // Add a white border around the circle
      marginRight: '10px',
    };
    
    return (
      <div style={midSectionStyle}>

    
        <div style={contentContainer}>
    
        {/* 포스터 div */}
          <div style={contentStyle}>
            <img src={posterUrl} alt={movieDetail.title} style={posterStyle} />
          </div>
    
        {/* OTT 유무 & 줄거리 div */}
          <div style={{ flex: 1 }}>
          <div variant="body1" style={ottInfoStyle}>
          {ottPlatforms.map((platform) => (
              <img
                key={platform}
                src={`images/logo/${platform}.png`}
                alt={platform}
                style={{ width: "50px", marginRight: "10px" }}
              />
            ))}
              </div>
              <div style={ottUnderline}/>

            <Typography variant="button" style={plotTextStyle}>
              {movieDetail.plotText}
            </Typography>
          </div>
        </div>
    
    
      </div>
    );
  };

  export default MovieDetailMidSection;
