    import React, { useState , useEffect }  from 'react'
    import axios from 'axios';
    import { top20sfmoviesDB } from '../../axios/main/movieLogic';


    const MainTopSection = () => {
      
      const [top20Movies, setTop20Movies] = useState([]);
      const [currentIdx, setCurrentIdx] = useState(0);

    useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
      const res = await top20sfmoviesDB()
      console.log(res);
      setTop20Movies(res)
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    }
        getTop20SfMovies()
      }, []);

      
      const handleNext = () => {
        setCurrentIdx((prevIdx) => (prevIdx + 1) % top20Movies.length);
      };

      return (
        <>
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h2>Top 20 Science Fiction Movies</h2>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <button onClick={handleNext}>Next</button>
        <div
          style={{
            display: 'flex',
            overflow: 'hidden',
          }}
        >
          {top20Movies.map((movie, index) => (
            <div
              key={index}
              style={{
                flex: '0 0 auto',
                marginRight: '10px',
                textAlign: 'center',
                display: index === currentIdx ? 'block' : 'none',
              }}
            >
              <img
                src={movie.poster}
                alt={movie.title}
                style={{ maxWidth: '200px', height: 'auto' }}
              />
              <p style={{ marginTop: '5px', fontSize: '14px' }}>{movie.title}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
        </>
      )
    }

    export default MainTopSection
