import { Avatar, Typography } from '@mui/material';
import React from 'react';

const MovieDetailMidSection2 = ({ movieDetail }) => {

  const midSection2Style = {
    marginLeft: '50px',
    marginTop: '50px',
    maxWidth: '1000px',
    minHeight: '300px',
    maxHeight: '400px',
    display: 'flex', // Add flex display to the main container
  }

  const staffRoleStyle = {
    marginTop: '50px',
    display: 'flex', 
    alignItems: 'center', 
  }

  const avatarStyle = {
    marginRight: '20px', 
  }

  return (
    <>
      <div style={midSection2Style}>
        <Typography variant='h4' style={{ fontSize: '25px' }}>출연 / 제작</Typography>
        <div style={staffRoleStyle}>
          {/* 감독 */}
          <Avatar style={avatarStyle} />
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
            <Typography variant='subtitle2' style={{ fontSize: '20px' }}>{movieDetail.directorNms}</Typography>
            <div style={{ marginTop: '5px' }}><Typography variant='button' style={{ opacity: '80%', fontSize: '15px' }}>감독</Typography></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default MovieDetailMidSection2;
