import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { top20sfmoviesDB } from '../../axios/main/movieLogic';
import Slider from 'react-slick';

const MainTopSection = () => {
  const [top20Movies, setTop20Movies] = useState([]);

  useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
        const res = await top20sfmoviesDB();
        setTop20Movies(res);
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    };

    getTop20SfMovies();
  }, []);




  
const settings = {
  dots: true,
  infinite: true,
  speed: 500,
  slidesToShow: 5,
  slidesToScroll: 5,
  initialSlide: 0,
  responsive: [
    {
      breakpoint: 1024,
      settings: {
        slidesToShow: 3,
        slidesToScroll: 3,
      }
    },
    {
      breakpoint: 600,
      settings: {
        slidesToShow: 2,
        slidesToScroll: 2,
      }
    },
    {
      breakpoint: 480,
      settings: {
        slidesToShow: 1,
        slidesToScroll: 1,
      }
    }
  ]
};





  return (
    <div>
    <h2> Responsive </h2>
    <Slider {...settings}>
      <div>
        <h3>1</h3>
      </div>
      <div>
        <h3>2</h3>
      </div>
      <div>
        <h3>3</h3>
      </div>
      <div>
        <h3>4</h3>
      </div>
      <div>
        <h3>5</h3>
      </div>
      <div>
        <h3>6</h3>
      </div>
      <div>
        <h3>7</h3>
      </div>
      <div>
        <h3>8</h3>
      </div>
    </Slider>
  </div>
  );
};

export default MainTopSection;
