import React from 'react';
import { Typography } from '@mui/material';

const MovieDetailMidSection = ({ movieDetail, posterUrl }) => {
  const midSectionStyle = {
    width: '1525px',
    height: '1200px',
    backgroundColor: '#f5f5f5',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  const contentContainer = {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    width: '90%',
  };

  const contentStyle = {
    display: 'flex',
    alignItems: 'flex-start', // Align content to start
    marginBottom: '20px',
  };

  const posterStyle = {
    width: '300px',
    height: '450px',
    objectFit: 'cover',
    marginRight: '50px',
  };
 
  const ottInfoStyle = {
    display: 'flex',
    alignItems: 'flex-end', // Align items to the bottom
    width: '100px',
    marginBottom: '10px',
    borderBottom: '1px solid black',
  };

  const plotTextStyle = {
    fontSize: '15px',
    opacity: 0.8,
    marginTop: 'auto',
  };

  return (
    <div style={midSectionStyle}>
      <div style={contentContainer}>
        <div style={contentStyle}>
          <img src={posterUrl} alt={movieDetail.title} style={posterStyle} />
          <div style={{ flex: 1 }}>
            <Typography variant="button" style={plotTextStyle}>
              {movieDetail.plotText}
            </Typography>
            <div style={ottInfoStyle}>
              ott
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieDetailMidSection;
