import { Avatar, Typography } from '@mui/material';
import React from 'react';

const MovieDetailMidSection2 = ({ movieDetail }) => {
  const midSection2Style = {
    marginLeft: '80px',
    marginTop: '50px',
    maxWidth: '1000px',
    minHeight: '300px',
    maxHeight: '400px',
    display: 'flex',
    flexDirection: 'column', // 세로로 정렬
  };

  const staffRoleStyle = {
    marginTop: '50px',
    display: 'flex',
    flexDirection: 'row', 
    alignItems: 'center', 
  };

  const avatarStyle = {
    marginTop: '5px',
    marginRight: '10px',
  };

  const directorStyle = {
    maxWidth: '300px',
    display: 'flex',
    alignItems: 'center',
    marginLeft : '10px',

  };

  const underlineStyle = {
    flex:1,
    borderBottom: '1px solid black',
    opacity: '10%',
  };

  // 먼저 movieDetail이 존재하고, directorNms 속성이 존재하는지 확인합니다.
  const directors = movieDetail && movieDetail.directorNms ? movieDetail.directorNms.split(',') : [];

  return (
    <>
      <div style={midSection2Style}>
        <Typography variant="h4" style={{ fontSize: '25px' }}>
          출연 / 제작
        </Typography>
      
        <div style={staffRoleStyle}>
          {directors.map((director, index) => (
            <div key={index} style={directorStyle}>
              <Avatar style={avatarStyle} />
              <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
                <Typography variant="subtitle2" style={{ fontSize: '16px' }}>
                  {director.trim()} {/* 앞뒤 공백 제거 */}
                </Typography>
                <div style={{ marginTop: '5px' }}>
                  <Typography variant="button" style={{ opacity: '60%', fontSize: '15px' }}>
                    감독
                  </Typography>
                </div>
              </div>
            </div>
          ))}
        </div>
          <div style={underlineStyle}/>
     
     
      </div>
    </>
  );
};

export default MovieDetailMidSection2;
