import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { top20sfmoviesDB } from '../../axios/main/movieLogic';
import Slider from 'react-slick';
import { Card, CardContent, Container, Typography } from '@mui/material';

const MainTopSection = () => {
  const [top20Movies, setTop20Movies] = useState([]);

  useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
        const res = await top20sfmoviesDB();
        setTop20Movies(res);
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    };

    getTop20SfMovies();
  }, []);




  
  const settings = {
    dots: true,
    arrows : true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5
  };





  return (
    <Container>
    <Typography variant="h4" align="center" mt={4}>Top 20 SF 영화</Typography>
    <Slider {...settings}>
      {top20Movies.map((movie, index) => (
        <div key={index}>
          <Card sx={{ maxWidth: 200, mx: 2 }}>
            <img src={movie.poster} alt={movie.title} style={{ width: '100%', height: 'auto' }} />
            <CardContent>
              <Typography variant="subtitle1" align="center">{movie.title}</Typography>
            </CardContent>
          </Card>
        </div>
      ))}
    </Slider>
  </Container>
  );
};

export default MainTopSection;
