import React from 'react';
import { Typography } from '@mui/material';

const MovieDetailMidSection = ({ movieDetail, posterUrl }) => {
  const midSectionStyle = {
    width: '1525px',
    height: '1200px',
    backgroundColor: '#f5f5f5',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };

  const contentContainer = {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    width: '80%',
  };

  const contentStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom : '100px'
  };

  const posterStyle = {
    width: '300px',
    height: '450px',
    objectFit: 'cover',
    marginRight: '50px',
  };

  const plotTextStyle = {
    fontSize: '18px',
  };

  return (
    <div style={midSectionStyle}>
      <div style={contentContainer}>
        <div style={contentStyle}>
          <img src={posterUrl} alt={movieDetail.title} style={posterStyle} />
        </div>
        <div style={{ flex: 1 }}>
          <Typography variant="caption" style={plotTextStyle}>
            {movieDetail.plotText}
          </Typography>
        </div>
      </div>
    </div>
  );
};

export default MovieDetailMidSection;
