import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Card, CardContent, Grid, Typography } from '@mui/material';
import InfoIcon from '@mui/icons-material/Info';
import 'swiper/swiper-bundle.min.css'; // 수정된 부분
import 'swiper/components/navigation/navigation.min.css'; // 수정된 부분
import 'swiper/components/pagination/pagination.min.css'; // 수정된 부분
import { Navigation, Pagination } from 'swiper/react/swiper-react.esm'; // 수정된 부분

const MovieCarousel = ({ top20Movies }) => {
  const settings = {
    navigation: true,
    pagination: { clickable: true },
    spaceBetween: 10,
    slidesPerView: 'auto',
    loop: true,
  };

  return (
    <Swiper {...settings}>
      {top20Movies.map((movie, index) => (
        <SwiperSlide key={index} style={{ position: 'relative' }}>
          <div style={{ position: 'absolute', top: 0, left: 0, background: 'black', color: 'white', padding: '4px 8px' }}>
            {index + 1}
          </div>
          <Card sx={{ maxWidth: 250, height: 430, mx: 2, border: 'none', margin: '0 auto' }}>
            <img src={movie.poster} alt={movie.title} style={{ width: '100%', height: '350px', borderRadius: '3px 3px 3px 3px' }} />
            <CardContent style={{ border: 'none', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start' }}>
              <Typography variant="subtitle2" sx={{ fontSize: 15 }}>{movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}</Typography>
              <Grid container justifyContent="flex-start">
                <Typography variant="body2">{movie.prodYear} · {movie.nation}</Typography>
              </Grid>
            </CardContent>
          </Card>
        </SwiperSlide>
      ))}
      <Navigation />
      <Pagination />
    </Swiper>
  );
};

export default MovieCarousel;
