import { Card, CardContent, Grid, Typography } from '@mui/material'
import React, { useEffect, useState } from 'react'
import { getMovieCommentDB } from '../../axios/detail/contentsLogic';
import Slider from 'react-slick';

const MovieDetailBtmSection = ({ movieDetail }) => {
  const movieId = movieDetail.movieId;
  const movieSeq = movieDetail.movieSeq;
  const [movieCommentList , setMovieCommentList] = useState([])

  useEffect(() => {
  const getMovieComment = async () => {
    try {
     const res = await getMovieCommentDB(movieId , movieSeq)
     setMovieCommentList(res)
    } catch (error) {
      console.log("영화 코멘트 로드 실패 : " , error);
    }
  }
  getMovieComment()
  },[])


  {/* Style */}
  const settings = {
    dots: true,
    arrows : true,
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4
  };


  const btmSectionStyle = {
    marginLeft: '100px',
    marginTop: '30px',
    maxWidth: '1500px',
    minHeight: '750px',
    maxHeight: '1600px',
  } 

  const commentListStyle = {
    width :'1200px',
    height : '500px',
    marginTop : '30px'
  }

  const commentStyle ={
    marginRight : '20px'
  }
   
  return (
    <>

    <div style={btmSectionStyle}>
      <Typography variant="h4" style={{ fontSize: '25px' }}>
          코멘트
        </Typography>

        <div style={commentListStyle}>
      <Slider {...settings}>
      {movieCommentList.map((movieComment, index) => (
  <div key={index} style={{ position: 'relative' }}>
    <Card sx={{ maxWidth: 500, height: 350, mx: 2, border: 'none', margin: '0 auto' }}>
      <CardContent style={{ border: 'none', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {/* 원형 프로필 이미지 */}
        <div style={{ width: 80, height: 80, borderRadius: '50%', overflow: 'hidden', marginBottom: 10 }}>
          <img src={movieComment.userProfileImage} alt="프로필" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
        </div>
        {/* 사용자 이름 */}
        <Typography variant="subtitle2" sx={{ fontSize: 15 }}>
          {movieComment.userName}
        </Typography>
        {/* 코멘트 내용 */}
        <Typography variant="body2" style={{ marginTop: 10 }}>
          {movieComment.commentDetail}
        </Typography>
      </CardContent>
    </Card>
  </div>
))}

    </Slider>
        </div>
    </div>




    </>
  )
}

export default MovieDetailBtmSection
