import React, { useEffect, useState } from "react";
import axios from "axios";
import config from "../../config";
import { serialize } from "cookie";
import { Alert, AlertTitle } from "@mui/material";
import { Navigate, useLocation, useNavigate } from "react-router";

const NaverLogin = () => {
  const [loginAlert, setLoginAlert] = useState(null);
  const [naverLogin, setNaverLogin] = useState(null); // 네이버 로그인 상태 추가
  const location = useLocation();
  const navigate = useNavigate();

  const getNaverToken = () => {
    if (!location.hash) return;
    const token = location.hash.split("=")[1].split("&")[0];
    console.log(token);
    setNaverLogin(token); // 네이버 로그인 상태를 업데이트
  };

  const initializeNaverLogin = () => {
    const naverLogin = new window.naver.LoginWithNaverId({
      clientId: config.naverClientId,
      callbackUrl: config.naverRedirectUri,
      clientSecret: config.naverClientSecret,
      isPopup: false,
      loginButton: { color: "green", type: 3, height: "50" },
    });

    naverLogin.init();

    naverLogin.getLoginStatus(function (status) {
      if (status) {
        const { id, nickname, email, profile_image } = naverLogin.user;
        console.log("네이버 로그인 성공!");
        console.log("사용자 아이디:", id);
        console.log("사용자 닉네임:", nickname);
        console.log("사용자 이메일:", email);
        console.log("프로필 이미지 URL:", profile_image);
        document.cookie = serialize("userId", id, { path: "/" });

        // 서버로 토큰을 보내고 로그인 처리
        axios
          .post("http://localhost:8000/user/login/naver", { code: token }) // token 프로퍼티로 토큰 전달
          .then((response) => {
            // 로그인 성공 처리
            console.log("백엔드와 통신 성공:", response.data);
            setLoginAlert(
              <Alert severity="success" onClose={() => setLoginAlert(null)}>
                <AlertTitle>로그인 성공!</AlertTitle>
                <strong>메인페이지로 이동합니다</strong>
              </Alert>
            );
            setTimeout(() => {
              navigate("/main"); // 메인 페이지로 이동
            }, 2000);
          })
          .catch((error) => {
            // 통신이 실패한 경우의 처리
            console.error("백엔드와 통신 실패:", error);
            setLoginAlert(
              <Alert severity="warning" onClose={() => setLoginAlert(null)}>
                <AlertTitle>로그인 실패</AlertTitle>
                <strong>입력 정보를 다시 확인해주세요</strong>
              </Alert>
            );
          });
      } else {
        console.log("Naver 로그인 실패");
      }
    });
  };

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://static.nid.naver.com/js/naveridlogin_js_sdk_2.0.2.js";
    script.charset = "utf-8";
    script.onload = () => {
      initializeNaverLogin();
    };
    document.body.appendChild(script);

    getNaverToken();
  }, []);

  return (
    <div id="naverIdLogin" style={{ textAlign: "center" }}>
      {/* 추가적으로 필요한 JSX 코드 작성 */}
      {loginAlert}
    </div>
  );
};

export default NaverLogin;
