import * as React from 'react';
import Title from './Title';
import TheatersIcon from '@mui/icons-material/Theaters';
import { useEffect } from 'react';
import { releaseSoonMovieDB } from '../../axios/main/movieLogic';
import { useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import { useSnackbar } from 'notistack';
import { useNavigate } from 'react-router';
import { firebaseStorage } from '../../utils/firebase';

export default function ReleaseSoon({ userInfo }) {
  const navigate = useNavigate();
  const [releaseSoonMovies , setReleaseSoonMovies] = useState([])
  const [posterUrls, setPosterUrls] = useState([]);
  const [alertOn, setAlertOn] = useState(false)
  const { enqueueSnackbar } = useSnackbar(); 

  useEffect(() => {
  const getReleaseSoonMovie = async() => {
    try {
      const res = await releaseSoonMovieDB()
      setReleaseSoonMovies(res[0])
    } catch (error) {
      console.log('개봉 예정 영화 로드 실패 : ', error);
    }
  }
  getReleaseSoonMovie()
  },[])

  useEffect(() => {
    const getPosterUrls = async () => {
      const urls = await Promise.all(releaseSoonMovies.map(async (movie) => {
        const storageRef = firebaseStorage.ref(`poster/${movie.movieId}${movie.movieSeq}.jpg`);
        try {
          const url = await storageRef.getDownloadURL();
          return url;
        } catch (error) {
          console.log("포스터 URL 페칭 에러 : " , error);
          return null;
        }
      }));
      setPosterUrls(urls);
    };

    getPosterUrls();
  }, []);


  /* Style */
  const releaseSoonSectiontyle = {
    minWidth: '600px',
    maxWidth: '600px',
    minHeight: '150px',
    marginTop: '10px',
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'row'
  };

/*  
> */
  return (
    <>
    <div style={{display:'flex'}}>
      <TheatersIcon style={{marginLeft:'15px',marginRight:'5px' ,marginTop:'3px'}}/><Title>개봉 예정</Title>
    </div>

    <div style={releaseSoonSectiontyle}>
    <Card sx={{ maxWidth: 240, height: 250, mx: 2, border: 'none', marginRight: '10px', cursor: 'pointer'}}>
        <img
                src={posterUrls[index]}
                alt={movie.title}
                style={{ width: '100%', height: '250px', borderRadius: '3px 3px 3px 3px' ,cursor:'pointer'}}
                onClick={() => navigate(`/movieDetail/${movie.movieId}${movie.movieSeq}`)}
              />
            </Card>
    </div>
    </>
  );
}
