import React from 'react';

const MovieDetailTopSection = ({ imageUrl , movieDetail}) => {
  console.log(movieDetail);
  const topSectionStyle = {
    width: '1500px',
    height: '500px', // 고정된 높이를 설정
    overflow: 'hidden',
    position: 'relative'
  };

  const imageStyle = {
    display: 'block',
    width: '100%',
    height: '100%',
    objectFit: 'cover', // 이미지의 크기를 조절하여 화면에 맞춤
  };

  const overlayStyle = {
    position: 'absolute',
    bottom: 0,
    left: 0,
    width: '100%',
    padding: '20px',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Adjust transparency here
    color: 'white',
    fontWeight: 'bold',
  };


  return (
    <div style={topSectionStyle}>
      <img src={imageUrl} alt="Movie Stills" style={imageStyle} />
    </div>
  );
};

export default MovieDetailTopSection;
