import React, { useEffect, useState } from 'react';
import { directorInfoDB } from '../../axios/main/movieLogic';

const MovieDetailMidSection2 = ({ movieDetail }) => {
  const [directors, setDirectors] = useState([]);

  useEffect(() => {
    const fetchDirectorsInfo = async () => {
      const directorIds = movieDetail.directorIds ? movieDetail.directorIds.split(',') : [];

      const directorsInfo = await Promise.all(
        directorIds.map(async (directorId) => {
          if (directorId) {
            try {
              return await directorInfoDB(directorId); // 이 부분에 해당하는 함수를 호출해야 합니다.
            } catch (error) {
              console.log('감독 정보 로드 실패 : ', error);
              return null;
            }
          }
          return null;
        })
      );

      setDirectors(directorsInfo.filter(info => info !== null));
    };

    fetchDirectorsInfo();
  }, [movieDetail.directorIds]);

  const creditInfoStyle = {};

  return (
    <>
      <div style={creditInfoStyle}>
        <h3>감독 정보</h3>
        {directors.map((director, index) => (
          <div key={index}>
            <p>이름: {director?.directorNm}</p>
          </div>
        ))}
      </div>
    </>
  );
};

export default MovieDetailMidSection2;
