import React, { useState, useEffect } from 'react';
import { top20sfmoviesDB } from '../../axios/main/movieLogic';
import Slider from 'react-slick';
import { Card, CardContent, Grid, Typography} from '@mui/material';

const MainTopSection = () => {
  const [top20Movies, setTop20Movies] = useState([]);

  useEffect(() => {
    const getTop20SfMovies = async () => {
      try {
        const res = await top20sfmoviesDB();
        setTop20Movies(res);
      } catch (error) {
        console.error('top20영화 로드 실패:', error);
      }
    };

    getTop20SfMovies();
  }, []);




  
  const settings = {
    dots: true,
    arrows : true,
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5
  };





  return (
    <div>
    <Typography variant="h6" align="center" mt={4} sx={{marginRight: 160 , marginBottom:3 , fontSize:25}}>Top 20 SF 영화</Typography>
    <Slider {...settings}>
      {top20Movies.map((movie, index) => (
        <div key={index}>
          <Card sx={{ maxWidth: 250, height: 430 ,mx: 2 }}>
            <img src={movie.poster} alt={movie.title} style={{ width: '100%', height: '350px' }} />
            <CardContent style={{ display: 'flex', flexDirection: 'column', justifyContent: 'flex-start' }}>
                <Typography variant="subtitle2" sx={{fontSize:15}}>{movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}</Typography>
                <Grid container justifyContent="flex-start">
                  <Typography variant="body2">{movie.prodYear} ㅁ {movie.nation}</Typography>
                </Grid>
              </CardContent>
          </Card>
        </div>
      ))}
    </Slider>
  </div>
  );
};

export default MainTopSection;
