import React, { useEffect, useState } from 'react';
import Link from '@mui/material/Link';
import Title from './Title';
import { getUserArchiveDB } from '../../axios/detail/contentsLogic';
import { Card, CardContent, Grid, Typography } from '@mui/material';
import { firebaseStorage } from '../../utils/firebase';
import { useNavigate } from 'react-router';

export default function MovieArchive({ userInfo }) {
  const userId = userInfo.userId;
  const navigate = useNavigate();
  const [userMovieArchive, setUserMovieArchive] = useState([]); 
  const [posterUrls, setPosterUrls] = useState([]);

  useEffect(() => {
    const getUserArchive = async () => {
      try {
        const res = await getUserArchiveDB(userId);
        setUserMovieArchive(res);
      } catch (error) {
        console.log('유저 보관함 로드 실패 : ', error);
      }
    };
    if (userId !== undefined) {
      getUserArchive();
    }
  }, [userId]);


  useEffect(() => {
    const getPosterUrls = async () => {
      const urls = await Promise.all(userMovieArchive.map(async (movie) => {
        const storageRef = firebaseStorage.ref(`poster/${movie.movieId}${movie.movieSeq}.jpg`);
        try {
          const url = await storageRef.getDownloadURL();
          return url;
        } catch (error) {
          console.log("포스터 URL 페칭 에러 : " , error);
          return null;
        }
      }));
      setPosterUrls(urls);
    };

    getPosterUrls();
  }, [userMovieArchive]);

  {/* Style */}

  const archiveSectiontyle = {
    minWidth: '1200px',
    maxWidth: '1200px',
    minHeight: '500px',
    marginTop: '20px',
    display: 'flex',
    flexDirection: 'row', // 가로로 정렬
    flexWrap: 'wrap', // 요소가 너비를 초과할 경우 여러 줄로 나눠 정렬
  };

  return (
    <>
      <Title>보관함</Title>
      <div style={archiveSectiontyle}>
      {userMovieArchive.length > 0 ? ( 
        userMovieArchive.map((movie, index) => (
          <div key={index}>
            <Card
              sx={{
                maxWidth: 220,
                height: 450,
                mx: 2,
                border: 'none',
                marginRight: '10px',
                cursor: 'pointer',
              }}
              onClick={() => navigate(`/movieDetail/${movie.movieId}${movie.movieSeq}`)}
            >
              <img src={posterUrls[index]} alt={movie.title} style={{ width: '100%', height: '350px', borderRadius: '3px 3px 3px 3px' }} />
              <CardContent style={{ border: 'none', display: 'flex', flexDirection: 'column', justifyContent: 'flex-start' }}>
                <Typography variant="subtitle2" sx={{ fontSize: 15 }}>
                  {movie.title.length > 18 ? `${movie.title.slice(0, 18)}...` : movie.title}
                </Typography>
                <Grid container justifyContent="flex-start" style={{marginTop:'5px'}}>
                <Typography variant="body2">{movie.addDate}</Typography>
              </Grid>
              </CardContent>
            </Card>
          </div>
        ))
      ) : (
        <p>아직 보관한 영화가 없습니다.</p>
      )}
      </div>
      <Link color="primary" href="/main" sx={{ mt: 3 }}>
        See more Movies
      </Link>
    </>
  );
}
